//姿樺
$("#image_member1").click(function() {
  $(this).animate({ "opacity": "1" }, 200);
  $("#image_member2").animate({ "opacity": "0.3" }, 200);
  $("#image_member3").animate({ "opacity": "0.3" }, 200);
  $("#image_member4").animate({ "opacity": "0.3" }, 200);
  $("#image_member5").animate({ "opacity": "0.3" }, 200);
  $(".person_word1").text("Zue-Hua Lee");
  $(".person_word2").text("Programmer");
  $(".person_word2").animate({right:'50vw'},0);
  $(".person_word3").text("“Logic will get you from A to B,");
  $(".person_word4").text("Imagination will take you everywhere.”");
  $("#image4").css({ "background": "url(https://ppt.cc/fS2fsx@.png) no-repeat bottom left/contain" });

  $("#illustration1~.illustration_word").text("台灣,新北"); //兄弟元素選擇器用~
  $("#illustration2~.illustration_word").text("電機工程學系 - 大學部三年級");
  $("#illustration3~.illustration_word").text("12月11日");

  $(".person_word6").html("<p>我是網頁設計的新手，雖然還有很多需要學習，</p><p>但我樂於接受挑戰，對於遇到的問題會一步步地嘗試解決，</p><p>花時間研究和探索新的技術和工具，並將它們應用到自己的創作當中，</p><p>在不斷地實踐中成長，希望未來能成為優秀的網頁設計師。</p>");
  $(".world_map").css({ "background": "url(https://ppt.cc/fGbfkx@.png) no-repeat center/contain" });
});

