//floating animation
$(document).ready(function() {
  $('.main_word1').transition('fade up', 2000)
  $('.main_word2').transition('fade up', 2000)
  $('#logo').transition('fade up', 2000)
  $('.title_word1').transition('fade up', 2500)
  $('.title_word2').transition('fade up', 2500)
})

let display = false;
$(window).scroll(function() {
  var now = $(window).scrollTop() + 400;
  var target = $('#WeLugg_aboutCompany').offset().top;

  if (!display && now > target) {
    $('.aboutCompany_word1').transition('fade up', 2000)
    $('.aboutCompany_word2').transition('fade up', 2000)
    $('#image_left').transition('fade up', 2000)
    $('#image_mid').transition('fade up', 2000)
    $('#image_right').transition('fade up', 2000)
    $('.aboutCompany_word3').transition('fade up', 2000)
    $('.aboutCompany_word4').transition('fade up', 2000)
    display = true
  }
});

